mapper.select:
select *
from (
select rownum rnum , a.*, (select nvl(count(*),0) from comment2 where num = a.num) totalcount from
(select * from board2 where boardid = #{boardid} ${column}
order by num desc) a) where rnum BETWEEN #{start} and 	#{end}

