order by num desc) a) where rnum BETWEEN #{start} and 	#{end}

mapper.update:
UPDATE board
SET readcnt = readcnt + 1
WHERE num = #{num}

update liketable2
set likecount = likecount + 1
where boardnum = #{boardnum} AND nicname = #{nicname}
